import React, { useState, useEffect } from 'react';
import { Button, Form, FormGroup, FormText, FormFeedback, Label, Input } from 'reactstrap';

function FoodForm(props) {
  const [form, setForm] = useState({
    id: '',
    foodname: '',
    expirydate: '',
    person: '',
    phone: '',
  })

  const [error, setError] = useState([]);

  const onChange = e => {
      let tempError = []

      if(!e.target.value) {
      tempError.push(`${e.target.name} field cannot be empty`)
    }

    setForm({
      ...form,
      [e.target.name]: e.target.value,
    })

    setError(tempError)
  }

  const checkError = () => {
    return this.error === []
  }

  const submitFormAdd = async (e) => {
    const response = await fetch(`http://localhost:8080/api/food`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })

    const data = await response.json()
    props.addFood(data)
  }

  const submitFormEdit = async (e) => {
    const response = await fetch(`http://localhost:8080/api/food/${form.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        id: form.id,
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })
    const data = await response.json()
    props.updateFood(data)
  }

  useEffect(() => {
    if (props.item) {
      const { id, foodname, expirydate, person, phone } = props.item
      setForm({ id, foodname, expirydate, person, phone })
    }
  }, [form])

  return (
    <Form onSubmit={props.item ? submitFormEdit : submitFormAdd}>

      <FormGroup>
        <Label for="foodname">Food Name</Label>
        <Input type="text" name="foodname" id="foodname" onChange={onChange} value={form.foodname}
        invalid={checkError}/>
        {/* {checkEmptyFields ? <FormText color="danger">Required Field</FormText> : null} */}
      </FormGroup>
      <FormGroup>
        <Label for="expirydate">Expiry Date</Label>
        <Input type="date" name="expirydate" id="expirydate" onChange={onChange} value={form.expirydate} />
      </FormGroup>
      <FormGroup>
        <Label for="person">Person</Label>
        <Input type="person" name="person" id="person" onChange={onChange} value={form.person} />
      </FormGroup>
      <FormGroup>
        <Label for="phone">Phone</Label>
        <Input type="text" name="phone" id="phone" onChange={onChange} value={form.phone}/>
      </FormGroup>
      <Button color="primary" >Submit</Button>
    </Form>
  )
}

export default FoodForm
