import React, { useState, useEffect } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

function AddEditForm(props) {
  const [form, setValues] = useState({
    id: 0,
    foodname: '',
    expirydate: '',
    person: '',
    phone: '',
  })

  const onChange = e => {
    setValues({
      ...form,
      [e.target.name]: e.target.value
    })
  }

  const submitFormAdd = async (e) => {
    const response = await fetch(`http://localhost:8080/api/food`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })

    const data = await response.json()
    props.addItemToState(data)
  }

  const submitFormEdit = async (e) => {
    const response = fetch(`http://localhost:8080/api/food/${form.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        id: form.id,
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })
    const data = response.json()
    props.updateState(data)
  }

  useEffect(() => {
    if (props.item) {
      const { id, foodname, expirydate, person, phone, location, hobby } = props.item
      setValues({ id, foodname, expirydate, person, phone, location, hobby })
    }
  }, [])

  return (
    <Form onSubmit={props.item ? submitFormEdit : submitFormAdd}>
      <FormGroup>
        <Label for="foodname">foodname Name</Label>
        <Input type="text" name="foodname" id="foodname" onChange={onChange} value={form.foodname === null ? '' : form.foodname} />
      </FormGroup>
      <FormGroup>
        <Label for="expirydate">expirydate Name</Label>
        <Input type="text" name="expirydate" id="expirydate" onChange={onChange} value={form.expirydate === null ? '' : form.expirydate} />
      </FormGroup>
      <FormGroup>
        <Label for="person">person</Label>
        <Input type="person" name="person" id="person" onChange={onChange} value={form.person === null ? '' : form.person} />
      </FormGroup>
      <FormGroup>
        <Label for="phone">Phone</Label>
        <Input type="text" name="phone" id="phone" onChange={onChange} value={form.phone === null ? '' : form.phone} placeholder="ex. 555-555-5555" />
      </FormGroup>
      <FormGroup>
        <Label for="location">Location</Label>
        <Input type="text" name="location" id="location" onChange={onChange} value={form.location === null ? '' : form.location} placeholder="City, State" />
      </FormGroup>
      <FormGroup>
        <Label for="hobby">Hobby</Label>
        <Input type="text" name="hobby" id="hobby" onChange={onChange} value={form.hobby} />
      </FormGroup>
      <Button>Submit</Button>
    </Form>
  )
}

export default AddEditForm
