import React from 'react'
import { Table, Button } from 'reactstrap';
import FoodModal from '../Modals/FoodModal'

function FoodTable(props) {
  const deleteItem = async (id) => {
    let confirmDelete = window.confirm('Delete item?')
    if (confirmDelete) {
      props.deleteFood(id)
      return await fetch(`http://localhost:8080/api/food/${id}`, {
        method: 'DELETE'
      })
    }
  }

  const items = props.foodList.map(item => {
    return (
      <tr key={item.id}>
        <td>{item.foodname}</td>
        <td>{item.expirydate}</td>
        <td>{item.person}</td>
        <td>{item.phone}</td>
        <td>
          <div>
            <FoodModal buttonLabel="Edit Food" item={item} updateFood={props.updateFood} />
            <Button color="danger" size="sm" onClick={() => deleteItem(item.id)}>Delete</Button>
          </div>
        </td>
      </tr>
    )
  })

  return (
    <Table bordered dark responsive hover striped>
      <thead>
        <tr>
          <th>Food Name</th>
          <th>Expiry Date</th>
          <th>Person</th>
          <th>Phone</th>
          <th>Modify Food</th>
        </tr>
      </thead>
      <tbody>
        {items}
      </tbody>
    </Table>
  )
}

export default FoodTable
