import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'reactstrap'
import FoodModal from './Components/Modals/Modal'
import FoodTable from './Components/Tables/FoodTable'

function App(props) {

  const [items, setItems] = useState([])

  const getItems = async () => {
    var tempFoodList = []
    fetch('http://localhost:8080/api/food', {
      method: 'GET'
    })
      .then(response => response.json())
      .then(items => {
        for (var i = 0; i < items.data.length; i++) {
          tempFoodList.push({
            id: items.data[i]._id,
            foodname: items.data[i].foodname,
            expirydate: items.data[i].expirydate,
            person: items.data[i].person,
            phone: items.data[i].phone
          })

        }
        setItems(tempFoodList)
      })
  }

  const addItemToState = (item) => {
    setItems([...items, item])
  }

  const updateState = (item) => {
    const itemIndex = items.findIndex(data => data.id === item.id)
    const newArray = [...items.slice(0, itemIndex), item, ...items.slice(itemIndex + 1)]
    setItems(newArray)
  }

  const deleteItemFromState = (id) => {
    const updatedItems = items.filter(item => item.id !== id)
    setItems(updatedItems)
  }

  useEffect(() => {
    getItems()
  }, [items]);

  return (
    <Container className="App">
      <Row>
        <Col>
          <h1 style={{ margin: "20px 0" }}>CRUD Database</h1>
        </Col>
      </Row>
      <Row>
        <Col>
          <FoodTable items={items} updateState={updateState} deleteItemFromState={deleteItemFromState} />
        </Col>
      </Row>
      <Row>
        <Col>
          <FoodModal buttonLabel="Add Item" addItemToState={addItemToState} />
        </Col>
      </Row>
    </Container>
  )
}

export default App
