import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'reactstrap'
import FoodModal from './components/Modals/FoodModal'
import FoodTable from './components/Tables/FoodTable'

function App(props) {

  const [foodList, setFoodList] = useState([])

  const getfoodList = async () => {
    var tempFoodList = []
    fetch('http://localhost:8080/api/food', {
      method: 'GET'
    })
      .then(response => response.json())
      .then(foodList => {
        for (var i = 0; i < foodList.data.length; i++) {
          tempFoodList.push({
            id: foodList.data[i]._id,
            foodname: foodList.data[i].foodname,
            expirydate: foodList.data[i].expirydate,
            person: foodList.data[i].person,
            phone: foodList.data[i].phone
          })
        }
        setFoodList(tempFoodList)
      })
  }

  const addItemToState = (item) => {
    setFoodList([...foodList, item])
  }

  const updateState = (item) => {
    var tempFoodList = []
    tempFoodList = foodList.map(curr => curr.id === item.id ? item : curr)
    setFoodList(tempFoodList);
  }

  const deleteItemFromState = (id) => {
    var tempFoodList = []
    tempFoodList = foodList.filter(item => item.id !== id)
    setFoodList(tempFoodList)
  }

  useEffect(() => {
    getfoodList()
  }, [foodList]);

  return (
    <Container className="App">
      <Row>
        <Col>
          <h1 style={{ margin: "20px 0" }}>CRUD Database</h1>
        </Col>
      </Row>
      <Row>
        <Col>
          <FoodTable foodList={foodList} updateState={updateState} deleteItemFromState={deleteItemFromState} />
        </Col>
      </Row>
      <Row>
        <Col>
          <FoodModal buttonLabel="Add Item" addItemToState={addItemToState} />
        </Col>
      </Row>
    </Container>
  )
}

export default App
