import React, { useState, useEffect } from 'react';
import { Button, Form, FormGroup, FormText, FormFeedback, Label, Input } from 'reactstrap';

function FoodForm(props) {
  const [form, setForm] = useState({
    id: '',
    foodname: '',
    expirydate: '',
    person: '',
    phone: '',
  })

  const [error, setError] = useState([]);

  const handleChange = e => {
    let foodError = ''
    if ((e.target.value !== '')) {
      foodError = 'has-success';
    } else {
      foodError = 'has-danger';
    }

    setForm({
      ...form,
      [e.target.name]: e.target.value,
    })

    setError(foodError);
  }


  const submitFormAdd = async (e) => {
    const response = await fetch(`http://localhost:8080/api/food`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })

    const data = await response.json()
    props.addFood(data)
  }

  const submitFormEdit = async (e) => {
    const response = await fetch(`http://localhost:8080/api/food/${form.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        id: form.id,
        foodname: form.foodname,
        expirydate: form.expirydate,
        person: form.person,
        phone: form.phone,
      })
    })
    const data = await response.json()
    props.updateFood(data)
  }

  useEffect(() => {
    if (props.item) {
      const { id, foodname, expirydate, person, phone } = props.item
      setForm({ id, foodname, expirydate, person, phone })
    }
  }, [form])

  return (
    <Form onSubmit={props.item ? submitFormEdit : submitFormAdd}>

      <FormGroup>
        <Label for="foodname">Food Name</Label>
        <Input type="text" name="foodname" id="foodname" 
        onChange={(e) => {
                this.handleChange(e);
              }} 
        value={form.foodname}
        valid={error === 'has-success'}/>
        {/* {checkEmptyFields ? <FormText color="danger">Required Field</FormText> : null} */}
      </FormGroup>
      <FormGroup>
        <Label for="expirydate">Expiry Date</Label>
        <Input type="date" name="expirydate" id="expirydate" onChange={handleChange} value={form.expirydate} />
      </FormGroup>
      <FormGroup>
        <Label for="person">Person</Label>
        <Input type="person" name="person" id="person" onChange={handleChange} value={form.person} />
      </FormGroup>
      <FormGroup>
        <Label for="phone">Phone</Label>
        <Input type="text" name="phone" id="phone" onChange={handleChange} value={form.phone}/>
      </FormGroup>
      <Button color="primary" >Submit</Button>
    </Form>
  )
}

export default FoodForm
